package com.aistudio.lifehackstudio.data.networkDataSource.cardCompanyNetworkDataSourceimport android.util.Logimport com.aistudio.lifehackstudio.data.Apiimport com.aistudio.lifehackstudio.data.networkDataSource.ResultWrapperclass CardCompanyDataSourceImpl(private val api: Api) : CardCompanyDataSource {    override suspend fun getCardCompany(id: Int): ResultWrapper<List<ResponseCardCompany>> =        try {            val response = api.getCardCompany(id = id)            ResultWrapper.Success(value = response)        } catch (exception: Exception) {            Log.e("response card company", exception.toString())            ResultWrapper.Error(error = exception)        }}