package com.aistudio.lifehackstudio.data.networkDataSource.companyNetworkDataSourceimport android.util.Logimport com.aistudio.lifehackstudio.data.Apiimport com.aistudio.lifehackstudio.data.networkDataSource.ResultWrapperimport retrofit2.HttpExceptionclass CompanyDataSourceImpl(private val api: Api): CompanyDataSource {    override suspend fun getCompanyList(): ResultWrapper<List<ResponseCompany>> =        try {            val response = api.getCompany()            ResultWrapper.Success(value = response)        } catch (exception: HttpException) {            Log.e("response company", exception.toString())            ResultWrapper.Error(error = exception)        }}