package com.aistudio.lifehackstudio.ui.screens.componentsScreenimport androidx.compose.foundation.layout.*import androidx.compose.material.Buttonimport androidx.compose.material.CircularProgressIndicatorimport androidx.compose.material.Dividerimport androidx.compose.material.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.unit.dpimport com.aistudio.lifehackstudio.domain.cardCompanyRepository.CardCompanyimport com.aistudio.lifehackstudio.domain.companyRepository.Company@Composablefun CardCompany(modifier: Modifier, cardCompany: Company) {    Box(        modifier = modifier            .padding(vertical = 8.dp)            .fillMaxSize(),        contentAlignment = Alignment.Center    ) {        Column(modifier = modifier, horizontalAlignment = Alignment.CenterHorizontally) {            Text(                text = cardCompany.image.toString(),                modifier = modifier            )            Spacer(modifier = modifier.height(8.dp))            Text(text = cardCompany.name.toString(), modifier = modifier)            Divider(modifier = modifier.fillMaxWidth(), color = Color.LightGray)        }    }}@Composablefun DetailCardCompany(modifier: Modifier, cardDetail: CardCompany, onClickBack: () -> Unit) {    Box(modifier = modifier        .fillMaxSize()        .padding(10.dp), contentAlignment = Alignment.Center) {        Column(            modifier = modifier,            horizontalAlignment = Alignment.CenterHorizontally,            verticalArrangement = Arrangement.spacedBy(5.dp)        ) {            Text(text = cardDetail.name ?: "Нет данных об этом обьекте", modifier = modifier)            Text(text = cardDetail.img ?: "Нет данных об этом обьекте", modifier = modifier)            Text(text = cardDetail.description ?: "Нет данных об этом обьекте", modifier = modifier)            Text(text = cardDetail.lat.toString().ifBlank { "Нет данных об этом обьекте"}, modifier = modifier)            Text(text = cardDetail.lon.toString().ifBlank { "Нет данных об этом обьекте"}, modifier = modifier)            Text(text = cardDetail.phone ?: "Нет данных об этом обьекте", modifier = modifier)            Text(text = cardDetail.www ?: "Нет данных об этом обьекте", modifier = modifier)            Button(                onClick = { onClickBack() },                modifier = modifier                    .padding(top = 30.dp)            ) {                Text(text = "Back")            }        }    }}@Composablefun FullScreenLoaderComponent(modifier: Modifier) {    Box(modifier = modifier.fillMaxSize(), contentAlignment = Alignment.Center) {        CircularProgressIndicator()    }}