package com.aistudio.lifehackstudio.uiimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.navigation.NavTypeimport androidx.navigation.compose.NavHostimport androidx.navigation.compose.composableimport androidx.navigation.compose.rememberNavControllerimport androidx.navigation.navArgumentimport com.aistudio.lifehackstudio.ui.screens.cardCompanyScreen.CardCompanyScreenimport com.aistudio.lifehackstudio.ui.screens.companyScreen.СompanyScreen@Composablefun Navigation(modifier: Modifier) {    val navController = rememberNavController()    NavHost(navController = navController, startDestination = "companyScreen") {        composable(route = "companyScreen") {            СompanyScreen(                modifier = modifier,                navController = navController,            )        }        composable(            route = "cardCompanyScreen/{id}",            arguments = listOf(navArgument("id") { type = NavType.StringType })        ) { backStackEntry ->            backStackEntry.arguments?.getString("id")?.let { id ->                CardCompanyScreen(                    modifier = modifier,                    id = id,                    navController = navController                )            }        }    }}